import { GoogleGenAI, Content, Part } from "@google/genai";
import type { ChatMessage, Character, Mood, SavedChat, JournalEntry } from '../types';

const API_KEY = process.env.API_KEY;

if (!API_KEY) {
  throw new Error("API_KEY environment variable not set");
}

const ai = new GoogleGenAI({ apiKey: API_KEY });

// Helper function to extract base64 data and mime type from a data URL
const getFileData = (dataUrl: string) => {
    const match = dataUrl.match(/^data:(.+);base64,(.+)$/);
    if (!match) {
        throw new Error("Invalid data URL for image.");
    }
    const mimeType = match[1];
    const data = match[2];
    return { mimeType, data };
};


// Helper to format our chat history for the Gemini API
const formatHistoryForGemini = (history: ChatMessage[]): Content[] => {
  // Filter out the AI's welcome message, which has no conversational value for the model
  const conversationalHistory = history.filter(msg => msg.id !== 'welcome-message');

  // Fix: Explicitly type the map's return value to align with the filter's type guard.
  return conversationalHistory.map((msg): Content | null => {
    const parts: Part[] = [];

    // Add image part first if it exists (for user messages) for better multimodal performance
    if (msg.imageUrl && msg.sender === 'user') {
        try {
            const { mimeType, data } = getFileData(msg.imageUrl);
            parts.push({
                inlineData: {
                    mimeType,
                    data,
                },
            });
        } catch (e) {
            console.error(`Skipping invalid image in history: ${e}`);
        }
    }

    // Add text part if it's not empty
    if (msg.text && msg.text.trim() !== '') {
        parts.push({ text: msg.text });
    }
    
    // If a message has no valid parts (e.g. an empty text message from the user without an image), filter it out
    if (parts.length === 0) {
        return null;
    }

    return {
      role: msg.sender === 'user' ? 'user' : 'model',
      parts,
    };
  }).filter((c): c is Content => c !== null); // Filter out nulls and type guard
};


export async function getAiStreamedResponse(
  character: Character, 
  mood: Mood | null, 
  chatHistory: ChatMessage[]
) {
  try {
    let dynamicSystemInstruction = character.systemInstruction;
    if (mood) {
      dynamicSystemInstruction += `\n\nتذكير مهم: المستخدمة بدأت هذه المحادثة وهي تشعر بـ "${mood.name}". حافظي على نبرة متفهمة ومتعاطفة مع حالتها المزاجية طوال الحوار.`;
    }
    
    const contents: Content[] = formatHistoryForGemini(chatHistory);
    
    if (contents.length === 0) {
       throw new Error("No valid conversational content to send to the AI.");
    }

    const result = await ai.models.generateContentStream({
      model: 'gemini-2.5-flash',
      contents: contents,
      config: {
        systemInstruction: dynamicSystemInstruction,
      },
    });

    return result;

  } catch (error) {
    console.error("Error getting AI response:", error);
    throw new Error("Failed to get response from the AI.");
  }
}

export async function generateMoodAwareWelcomeMessage(character: Character, mood: Mood): Promise<string> {
  try {
    const prompt = `أنت تلعب دور شخصية اسمها '${character.name}'. شخصيتك محددة بالتعليمات التالية: "${character.systemInstruction}".

تحيتك القياسية هي: "${character.welcomeMessage}".

المستخدمة التي تتحدث معها أشارت للتو إلى أنها تشعر بأنها ${mood.name} (${mood.icon}).

مهمتك هي إعادة صياغة تحيتك القياسية لتعكس حالتها المزاجية مع الحفاظ على شخصيتك تمامًا. يجب أن تكون التحية الجديدة قصيرة ومتعاطفة وتنتهي بسؤال مفتوح لبدء المحادثة. تحدث بنفس اللهجة والنبرة المحددة لشخصيتك. لا تضف أي مقدمات أو شروحات. قدم فقط التحية المعاد صياغتها.`;

    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
      config: {
        temperature: 0.7, 
      }
    });

    const newWelcomeMessage = response.text.trim();
    
    if (newWelcomeMessage) {
      return newWelcomeMessage;
    } else {
      return character.welcomeMessage;
    }

  } catch (error) {
    console.error("Error generating mood-aware welcome message:", error);
    return character.welcomeMessage;
  }
}

export async function generateChatSummary(characterName: string, chatHistory: ChatMessage[]): Promise<string> {
  try {
    // Remove the initial welcome message and format the history for the prompt
    const formattedHistory = chatHistory
      .slice(1) 
      .map(msg => {
          let content = `${msg.sender === 'user' ? 'المستخدمة' : characterName}:`;
          if (msg.imageUrl) content += ' [صورة]';
          if (msg.text) content += ` ${msg.text}`;
          return content;
      })
      .join('\n');

    if (formattedHistory.length < 1) {
      return "لا توجد رسائل كافية لإنشاء ملخص.";
    }

    const prompt = `أنت مساعد متخصص في تلخيص المحادثات. قم بتلخيص المحادثة التالية بين مستخدمة وشخصية مساعدة اسمها '${characterName}'. يجب أن يكون الملخص موجزًا، محايدًا، ومجهول الهوية، ويركز على المواضيع الرئيسية التي نوقشت والنصائح المقدمة. لا تذكر اسم المستخدمة أو الشخصية المساعدة.

المحادثة:
${formattedHistory}`;

    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
    });
    
    return response.text;

  } catch (error) {
    console.error("Error generating summary:", error);
    throw new Error("Failed to generate summary from the AI.");
  }
}

export async function generateDailyChallenge(): Promise<string> {
    try {
        const prompt = "Generate a very short, simple, positive, one-sentence self-care challenge for a woman, in Arabic. The challenge should be an actionable task. Examples: 'Take 5 deep breaths', 'Write down one thing you are proud of', 'Listen to your favorite song without distractions'. Provide only the challenge text, nothing else.";
        const response = await ai.models.generateContent({
            model: 'gemini-2.5-flash',
            contents: prompt,
        });
        return response.text.trim();
    } catch (error) {
        console.error("Error generating daily challenge:", error);
        return "خذي 5 أنفاس عميقة اليوم."; // Fallback challenge
    }
}

export async function generateWeeklySummary(
  chats: SavedChat[],
  entries: JournalEntry[]
): Promise<string> {
  if (chats.length === 0 && entries.length === 0) {
    return "لم يتم العثور على أي نشاط هذا الأسبوع. ابدئي محادثة أو اكتبي في مفكرتك لتلقي ملخصك الأسبوعي القادم!";
  }

  let formattedData = "بيانات الأسبوع:\n";
  if (entries.length > 0) {
    formattedData += "\n--- مدخلات المفكرة ---\n";
    entries.forEach(entry => {
      formattedData += `العنوان: ${entry.title}\nالمحتوى: ${entry.content.substring(0, 200)}...\n\n`;
    });
  }
  if (chats.length > 0) {
    formattedData += "\n--- ملخصات المحادثات ---\n";
    chats.forEach(chat => {
      formattedData += `ملخص محادثة مع ${chat.character.name}: ${chat.summary}\n\n`;
    });
  }

  const prompt = `You are a gentle and insightful assistant. Analyze the following user-provided journal entries and chat summaries from the past week in Arabic. Identify recurring themes, emotions, and progress. Write a short, kind, and encouraging reflection for the user in Arabic. Do not give direct advice. Focus on acknowledging their feelings and efforts. Frame it as a gentle observation. Keep it under 100 words. Address the user directly but gently. Here is the data:\n\n${formattedData}`;

  try {
    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
    });
    return response.text;
  } catch (error) {
    console.error("Error generating weekly summary:", error);
    throw new Error("Failed to generate weekly summary.");
  }
}

// =============================
//      Sound Effects Service
// =============================

// A single Audio instance to prevent overlapping sounds and manage playback
let audio: HTMLAudioElement | null = null;

// Base64 encoded audio files. This prevents extra network requests.
const soundEffects = {
  // A subtle whoosh sound for sending a message
  sent: 'data:audio/wav;base64,UklGRlQNAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YUgNAAD//wA=/v8O/xH/If4f/0D/UPg//Iz+tfvT+Kf3U/Ir8Kfub9v3zQ/Tj79ft2+tX6QvoC+OL2svdy9yL2gvbi9pL24vbi9qL2wvai9rL2svay9rL2svay9qL2svai9qL2ovai9qL2ovai9qL2ovai9pL2gvdC92L2svfy+AL4UvjS+QL6AvpC+pL7Evu6/DL8evzY/QH+Mf5u/5H/3gA=',
  // A gentle pop for receiving a message
  received: 'data:audio/wav;base64,-==',
  // A positive chime for unlocking an achievement
  achievement: 'data:audio/wav;base64,UklGRlIAAABXQVZFZm10IBAAAAABAAIARKwAAIhYAQAEABAAAABkYXRhRAAAAP////8A/v/9/f7+/v39/v7+/f3+AAACAAEAD/8PAA4AEgAWABgAFQAPAAkAAAD+/Pz6/Pr8+vz7/Pz8/v4='
};

/**
 * Plays a sound effect from the pre-defined list.
 * @param key The key of the sound effect to play ('sent', 'received', 'achievement')
 */
const playSound = (key: keyof typeof soundEffects) => {
    try {
        if (!audio) {
            audio = new Audio();
        }
        audio.src = soundEffects[key];
        audio.play().catch(error => {
            // Autoplay is often restricted by browsers until a user interaction.
            // This error is expected and can be safely ignored in this context.
            if (error.name !== 'NotAllowedError') {
                console.error(`Error playing sound effect "${key}":`, error);
            }
        });
    } catch (error) {
        console.error("Error initializing audio:", error);
    }
};

export const playSentSound = () => playSound('sent');
export const playReceivedSound = () => playSound('received');
export const playAchievementSound = () => playSound('achievement');